      out.write("<script type=\"text/javascript\">\n");
      out.write("	function regist(){\n");
      out.write("		let formData = new FormData();\n");
      out.write("		\n");
      out.write("		formData.append(\"category_idx\", $(\"#category_idx\").val());\n");
      out.write("		formData.append(\"product_name\", $(\"#product_name\").val());\n");
      out.write("		formData.append(\"brand\", $(\"#brand\").val());\n");
      out.write("		formData.append(\"price\", $(\"#price\").val());\n");
      out.write("		formData.append(\"discount\", $(\"#discount\").val());\n");
      out.write("		\n");
      out.write("		// 체크박스 배열화해서 보내기!\n");
      out.write("		let sizeCheckArray=[];\n");
      out.write("		for(let i=0; i<$(\"input[name='size']\").length; i++){\n");
      out.write("			if($($(\"input[name='size']\")[i]).is(\":checked\")){\n");
      out.write("				sizeCheckArray.push($($(\"input[name='size']\")[i]).val());\n");
      out.write("			}\n");
      out.write("		}\n");
      out.write("		// 모아 놓은 배열 보내기! 파라미터 이름은 배열로 지정 !\n");
      out.write("		formData.append(\"size[]\", sizeCheckArray);\n");
      out.write("		\n");
      out.write("		// jQuery \n");
      out.write("		let colorCheckArray=[];\n");
      out.write("		$.each($(\"input[name='color']:checked\"), function(){\n");
      out.write("			colorCheckArray.push($(this).val());\n");
      out.write("		});\n");
      out.write("		formData.append(\"color[]\", colorCheckArray);\n");
      out.write("		\n");
      out.write("		formData.append(\"detail\", $(\"#detail\").val());\n");
      out.write("		\n");
      out.write("		\n");
      out.write("		console.log(fileList);\n");
      out.write("		for(let i=0;i<fileList.length;i++){\n");
      out.write("			let file = fileList[i];\n");
      out.write("			formData.append(\"file\", file);\n");
      out.write("		}\n");
      out.write("		\n");
      out.write("		\n");
      out.write("		$.ajax({\n");
