      out.write("		});\n");
      out.write("	}\n");
      out.write("</script>\n");
      out.write("<script type=\"text/babel\">\n");
      out.write("	let tag=[];	\n");
      out.write("	let previewRoot;\n");
      out.write("	let fileList=[];\n");
      out.write("	let oriFiles=[];\n");
      out.write("\n");
      out.write("	function ImgBox(props){\n");
      out.write("		return(\n");
      out.write("			<div className=\"col-sm-2 border\">\n");
      out.write("				<div>\n");
      out.write("					<a href=\"#\" onClick={(e)=>removeImg(e, props.index)}>X</a>\n");
      out.write("				</div>\n");
      out.write("				<img src={props.src} width='100px'/>\n");
      out.write("			</div>\n");
      out.write("		);\n");
      out.write("	}\n");
      out.write("\n");
      out.write("\n");
      out.write("	function removeImg(e, index){\n");
      out.write("		$(e.target).parent().parent().remove();\n");
      out.write("\n");
      out.write("		let file=oriFiles[index];\n");
      out.write("		let sel_index=oriFiles.indexOf(file);\n");
      out.write("\n");
      out.write("		// 구한 순번으로 배열 요소 삭제 \n");
      out.write("		oriFiles.splice(sel_index, 1);\n");
      out.write("	}\n");
      out.write("\n");
      out.write("\n");
      out.write("	function createCategoryOption(result){\n");
      out.write("		console.log(result);\n");
      out.write("		let op=\"<option value='0'>상품분류</option>\";\n");
      out.write("		\n");
      out.write("		for(let i=0; i<result.length;i++){ //json 배열만큼 반복\n");
      out.write("			\n");
      out.write("			let category=result[i];		//카테고리 하나 꺼내기\n");
      out.write("			op+=\"<option value='\"+category.category_idx+\"'>\"+category.category_name+\"</option>\";\n");
      out.write("		}\n");
      out.write("		$(\"#category_idx\").html(op);								\n");
      out.write("	}\n");
      out.write("\n");
      out.write("\n");
      out.write("	function getCategoryList(){\n");
      out.write("		$.ajax({\n");
      out.write("			url:\"/admin/category/category_list.jsp\",\n");
      out.write("			type:\"GET\",\n");
      out.write("			success:function(result, status, xhr){\n");
      out.write("				//옵션 채우기\n");
      out.write("				createCategoryOption(result);\n");
      out.write("			}\n");
      out.write("		});\n");
      out.write("	}\n");
      out.write("	\n");
      out.write("	function previewImg(){\n");
      out.write("		for(let i=0; i<fileList.length; i++){\n");
      out.write("			let reader=new FileReader();\n");
      out.write("\n");
      out.write("			reader.onload=function(e){\n");
      out.write("				tag.push(<ImgBox key={i} src={e.target.result} index={i} />);\n");
      out.write("\n");
      out.write("				if(i>=(fileList.length-1)){\n");
      out.write("					previewRoot.render(tag);\n");
      out.write("				}\n");
      out.write("			}\n");
      out.write("			reader.readAsDataURL(fileList[i]);\n");
      out.write("		}\n");
      out.write("	}\n");
      out.write("\n");
      out.write("	$(function(){\n");
      out.write("		// 리액트 객체 생성\n");
      out.write("		previewRoot=ReactDOM.createRoot(document.getElementById(\"preview\"));\n");
      out.write("		getCategoryList();\n");
      out.write("\n");
      out.write("		\n");
      out.write("		$(\"#file\").change(function(){\n");
      out.write("			oriFiles=fileList;			\n");
      out.write("\n");
      out.write("			for(let i=0; i<this.files.length; i++){\n");
      out.write("				fileList.push(this.files[i]);\n");
      out.write("			}\n");
      out.write("			previewImg();\n");
      out.write("			\n");
      out.write("		});\n");
      out.write("\n");
      out.write("		$(\"#bt_regist\").click(function(){\n");
      out.write("			regist();\n");
      out.write("		});\n");
      out.write("\n");
      out.write("		$(\"#bt_list\").click(function(){\n");
      out.write("			$(location).attr(\"href\",\"/admin/product/list.jsp\");\n");
      out.write("		});\n");
      out.write("		\n");
      out.write("	});\n");
      out.write("\n");
      out.write("</script>\n");
      out.write("\n");
